import React, { createContext, useContext, useState } from "react";
import eventsData from "./data.json";
import { Routes, Route } from "react-router-dom";
import Enough from "./pages/enough";
import Fundraisers from "./pages/fundraisers";
import "./style.css";
import Content, { Context } from "./components/Content";
import disableScroll from "./block-scroll.js"

type SetEventIndex = (index: number) => void;
type SetContentWarning = (index: boolean) => void;

interface Graphic {
    details: string;
    path:     string;
    level:   number;
}
let gallery: Graphic[][] = [[], [], [], [], []]

eventsData.forEach(graphic => {
    const level = graphic["level"];
    if (level == -1){
        return;
    }

    gallery[level - 1].push(graphic);
});

function getNextGraphic(){
    let i = 0;
    while (true) {
        const graphic = gallery[i].pop()
        if (graphic == undefined){
            i += 1;
            if (i == gallery.length){
                // Reset Gallery
            }
            continue;
        }

        return graphic;
    }
}

function MainContent({
    graphic,
    contentWarning,
    setEventIndex,
    setGraphic,
}: {
    eventIndex: number;
    contentWarning: boolean;
    setEventIndex: SetEventIndex;
    setContentWarning: SetContentWarning;
}) {
    const [isBlurred, setBlurred] = useState(true)

    function disableWarning(
        contentWarning: boolean,
        setContentWarning: SetContentWarning
    ) {
        return () => {
            scrollToGraphic();
            setContentWarning(false);
        };
    }
    function scrollToGraphic() {
        document.getElementById("graphic")?.style.setProperty("filter", "none")
        window.scrollTo({
            top: document.getElementById("worse-warn")?.offsetTop || 0,
            behavior: "smooth",
        });
        setBlurred(false); 
    }
    function scrollToEnough() {
        const element = document.getElementById("enough-title")!;
        element.scrollIntoView({ behavior: "smooth" });
    }

    return (
        <div id="page-container">
            <div id="title-container">
                <p id="main-title">
                    TRUE FACE
                    <br />
                    OF PALESTINE
                </p>
                <p id="main-subtitle">
                    Like every war in the conflict, this one too started after a palestinian terror attack.
                    Hamas took over civilian towns, commiting some of the most horrifying atrocities of the 21st century.
                </p>

                {contentWarning && (
                    <>
                        <div id="warning-containter" onClick={disableWarning(contentWarning, setContentWarning)}>
                            <img id="eye" src="../images/eye.png" />
                            <p id="content-warning">
                                CONTENT WARNING: EXTREME VIOLENCE
                                <br />
                            </p>
                            <p id="btn-disable-warning">
                                SHOW
                            </p>
                        </div>
                    </>
                )}
            </div>
            <p id="worse-warn">
                FOOTAGE WILL GET PROGRESSIVELY MORE GRAPHIC
            </p>
            <main id="graphic-container">
                <Context.Provider value={isBlurred}>
                    <Content
                        src={eventsData[eventIndex].path}
                        details={eventsData[eventIndex].details}
                    />
                </Context.Provider>
                <p id="graphic-detail">{eventsData[eventIndex].details}</p>
            </main>
            <div className="btn-container">
                <button id="btn-stop" onClick={scrollToEnough}>
                    STOP
                </button>
                <button
                    onClick={incIndex(eventIndex, setEventIndex)}
                    id="btn-more"
                >
                    WITNESS MORE
                </button>
            </div>
        </div>
    );
}

function incIndex(eventIndex: number, setEventIndex: SetEventIndex) {
    return () => {    
        document.getElementById("graphic-container")?.style.setProperty("opacity", "0%");
        setTimeout(() => {
            setEventIndex((eventIndex + 1) % eventsData.length)
            setTimeout(() => {
                document.getElementById("graphic-container")?.style.setProperty("opacity", "100%");
            }, 1000)
        }, 1000)

        // const step = Math.ceil(Math.random() * MEAN_STEP * 2);
        // if (eventIndex + step >= eventsData.length) {
        //     let i = 0;
        //     for (i = eventsData.length - 2; i >= 0; --i) {
        //         if (
        //             eventsData[i].level !=
        //             eventsData[eventsData.length - 1].level
        //         ) {
        //             break;
        //         }
        //     }
        //     if (i == eventIndex) {
        //         if (i + 1 < eventsData.length) {
        //             ++i;
        //         } else if (i > 0) {
        //             --i;
        //         }
        //     }
        //     setEventIndex(i);
        //     setEventIndex(eventIndex + 1);
        // }
        // setEventIndex(eventIndex + step);
    };
}



disableScroll();

const Homepage = () => {
    const [graphic, setGraphic] = useState(getNextGraphic());
    const [contentWarning, setContentWarning] = useState(true);

    return (
        <>
            <MainContent
                graphic={graphic}
                contentWarning={contentWarning}
                setContentWarning={setContentWarning}
                setGraphic={setGraphic}
            ></MainContent>
            <Enough></Enough>
        </>
    );
};

export default function App() { 
    window.addEventListener('load', () => {
        window.scrollTo(0, 0);
    });
    return (
        <div>
            <Routes>
                <Route path="/" element={<Homepage />} />
                <Route path="enough" element={<Enough />} />
                <Route path="fundraisers" element={<Fundraisers />} />
            </Routes>
        </div>
    );
}
